import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5001';

function App() {
  const [courses, setCourses] = useState([]);
  const [newCourse, setNewCourse] = useState('');
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [newAssignment, setNewAssignment] = useState('');
  const [newComment, setNewComment] = useState('');

  useEffect(() => {
    fetchCourses();
  }, []);

  const fetchCourses = async () => {
    const { data } = await axios.get(`${API_URL}/courses`);
    setCourses(data);
  };

  const addCourse = async () => {
    const { data } = await axios.post(`${API_URL}/courses`, { name: newCourse });
    setCourses([...courses, data]);
    setNewCourse('');
  };

  const addAssignment = async (courseId) => {
    const { data } = await axios.post(`${API_URL}/courses/${courseId}/assignments`, { title: newAssignment });
    setCourses(courses.map((c) => (c._id === courseId ? data : c)));
    setNewAssignment('');
  };

  const addComment = async (courseId, assignmentId) => {
    const { data } = await axios.post(`${API_URL}/courses/${courseId}/assignments/${assignmentId}/comments`, {
      comment: newComment,
    });
    setCourses(courses.map((c) => (c._id === courseId ? data : c)));
    setNewComment('');
  };

  return (
    <div>
      <h1>Course Management</h1>
      <input
        type="text"
        placeholder="Add new course"
        value={newCourse}
        onChange={(e) => setNewCourse(e.target.value)}
      />
      <button onClick={addCourse}>Add Course</button>
      <div>
        {courses.map((course) => (
          <div key={course._id} style={{ border: '1px solid black', margin: '10px', padding: '10px' }}>
            <h2>{course.name}</h2>
            <input
              type="text"
              placeholder="Add new assignment"
              value={newAssignment}
              onChange={(e) => setNewAssignment(e.target.value)}
            />
            <button onClick={() => addAssignment(course._id)}>Add Assignment</button>
            {course.assignments.map((assignment) => (
              <div key={assignment._id} style={{ marginLeft: '20px' }}>
                <h3>{assignment.title}</h3>
                <input
                  type="text"
                  placeholder="Add new comment"
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                />
                <button onClick={() => addComment(course._id, assignment._id)}>Add Comment</button>
                <ul>
                  {assignment.comments.map((comment, index) => (
                    <li key={index}>{comment}</li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
